<?php include_once('config/config.php');			if(!empty($_GET['r']))		{			$file=$_GET[r];			$params = explode('/',$config['Controller'].$file);			$clase=$params[1];			$metodo=$params[2].'Controller';			if(file_exists($config['Controller'].$params[1].'.php')){				include_once($config['Controller'].$params[1].'.php');					if(class_exists("$params[1]"))						{								$data = '';							foreach($params as $k =>$v):								if($k>=3){									$algo=explode("=",$v);									$data[$algo[0]]=$algo[1];								}							endforeach;							$cla =& new $clase();							if(method_exists($cla,$metodo)){								if(!array_key_exists("",$data))								{									$cla->$metodo($data);								}								else								{									$cla->$metodo();								}															}							else							{								echo $config['404'];							}						}			}else{				echo $config['404'];			}			}else{			header('Location:index.php?r='.$config['init_page'].'/index/');		}?>